import React from "react";
import '../../styles/dashboard/dash_view.css'
import '../../styles/login_themes.css'
// import SimpleBar from 'simplebar-react';
import SimpleBar from "simplebar-react";
import 'simplebar/dist/simplebar.min.css';

import Lesson_widget from "../lesson_widget";
import serverPath from "../../utilis/server-path";

class Dashboard extends React.Component {

    constructor(props) {
        super(props);
        this.theme_id = Math.floor(Math.random() * (31 - 1)) + 1;
    }


    async GetLatest() {
        try {
            const resp = await fetch(serverPath() + 'api/Lessons/latest.php', {
                method:'GET',
                headers: {
                    'Access-Control-Allow-Origin': '*'
                },
                credentials: 'include'
            }).then(data => data.json()).catch(null)
            if (resp !== null) {
                if (resp['CODE'] === 'OK') {
                    const tab = []
                    for (let i = 0; i < resp['Lessons']; i++) {
                        tab.push(<Lesson_widget ID={resp['Lessons'][i]['ID']} Name={resp['Lessons'][i]['Subject']}/>)
                    }
                    return tab
                }
                else return []
            } else return []
        } catch (e) {
           return []
        }
    }

    RenderLatest() {
        const tab = []
        const data = []
        this.GetLatest().then(e => tab)
        for (let i = 0; i < tab.length; i++) {
            data.push(<Lesson_widget ID={tab[i]['ID']} Name={tab[i]['Subject']}/>)
        }
        return data
    }

    render() {
        return (
            // <SimpleBar forceVisible="y" autoHide={false} style={{width: '800px'}}>
                <div className={"Dash_main_view"}>
                    <div className={"Dash_main_view_head theme_" + this.theme_id.toString() }>
                        <h1>Platforma Edukacyjna</h1>
                    </div>
                    <div className={'Dash_main_view_hello_view'}>
                        <h1 >Witaj <b>Michał Ratajewski</b> na <b>Platformie edukacyjnej</b>!</h1>
                    </div>
                    <div className={"Dash_main_view_last_lesson"}>
                        <h2>Najnowsze lekcje</h2>
                        <SimpleBar forceVisible="x" autoHide={false} style={{width: '99%', left: '50%', transform: 'translate(-50%, 0)'}}>
                            <div className={"Dash_main_view_last_lesson_list"}>
                                {/*<Lesson_widget/>*/}
                                {
                                    this.GetLatest()
                                }
                            </div>
                        </SimpleBar>
                    </div>
                </div>
            // </SimpleBar>

        )
    }
}

export default Dashboard;
