import React from "react";
import '../styles/lesson_view.css';

export default class Lesson extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
             ID: 0
        }
        this.initilizeParams()
    }

    initilizeParams() {
        console.log("Reading")
        const params = new URLSearchParams(window.location.search)
        let cos = parseInt(params.get('ID'))

        this.setState({ID: 1}, () => {
            console.log('Update')
        })

        console.log(cos , 'State: ' + JSON.stringify(this.state))
    }

    render() {
        return(
            <div>
                <div>
                    <h1>Lekcja {this.state.ID} </h1>
                </div>
            </div>
        )
    }
}
