import './App.css';
import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate} from 'react-router-dom';
import Welcome_page from "./windows/welcom_page";
import Main from './windows/main';
import Lessons from "./windows/lessons";
import authorize from './utilis/authorize'

class App extends React.Component {

    constructor() {
        super();
        this.state = {
            Logged: false,
            User_data: null
        }
    }

    componentDidMount() {
        authorize.CheckAuthorize().then(
            data => this.setState({Logged: data !== null, User_data: data !== null ? data : null})
        )
    }

    render() {
        return (
            <Router>
                <Routes>
                    <Route exact path='/' render={() =>{return (this.state.Logged ? <Navigate to="/lessons"/> : <Welcome_page/>)}} />
                    <Route exact path='/dashboard' render={() => {
                        return (this.state.Logged ? <Main User_data={this.state.User_data}/> : <Navigate to="/"/>)
                    }}/>
                    <Route exact path='/lessons' render={() => {
                        return (this.state.Logged ? <Lessons User_data={this.state.User_data}/> : <Navigate to="/"/>)
                    }}/>
                </Routes>
            </Router>
        );

    }
}

export default App;
