export default {

    async authorize(login_input, password_input) {
        const resp = await fetch('http://192.168.0.7:8080/api/auth/login.php', {
          method: 'POST',
          headers: {
              'Access-Control-Allow-Origin':'*',
              'Content-Type': 'application/json'
          },
          body: JSON.stringify({login: login_input, password: password_input})
        }).then(data => console.log(data.ok)).catch(null)

        if (resp !== null) {
            if (resp["Mess"] === 'OK') {
                window.sessionStorage.setItem('Token', resp['Token'])
                return true
            } else {
                return false
            }
        } else return false;
    }
}
