import './App.css';
import {Component} from "react";
import { BrowserRouter as Router, Route, Routes, Navigate} from 'react-router-dom';
import Welcome_page from "./windows/welcom_page";
import Main from './windows/main';
import Lessons from "./windows/lessons";
import authorize from './utilis/authorize'

class ProtectedRoute extends Component {

    render() {
        const {component: Component, ...props} = this.props
        console.log(this.props)
        return (
            <Route
                {...props}
                element={props => (
                    this.props.authenticated ?
                        <Component {...props} /> :
                        <Navigate to='/'/>
                )}
            />
        )
    }
}

class App extends Component {

    constructor() {
        super();
        this.state = {
            Logged: false,
            User_data: null
        }
    }

    componentDidMount() {
        authorize.CheckAuthorize().then(
            data => this.setState({Logged: data !== null, User_data: data !== null ? data : null})
        )
    }

    render() {
        return (
            <Router>
                {!this.state.Logged ?  <Routes>
                    <Route path='/' element={<Welcome_page Logged={this.state.Logged}/>}/>
                    <Route path='*' element={<Navigate replace to={'/'}/>}/>
                    {/*<Route path={'*'} >*/}
                    {/*    <Navigate replace to={'/'}/>*/}
                    {/*</Route>*/}
                    {/*<Route path='/dashboard' element={<Main User_data={this.state.User_data}/>}/>*/}
                    {/*<Route path='/lessons' element={<Lessons User_data={this.state.User_data}/>}/>*/}


                    {/*{this.state.Logged ? <Route path='/' element={<Welcome_page/>}/> :  <Navigate to="/dashboard"  />}*/}
                    {/*<Route  path='/dashboard' element={this.state.Logged ? <Main User_data={this.state.User_data}/> : <Navigate to="/"  />}/>*/}
                    {/*<Route  path='/lessons' element={this.state.Logged ? <Lessons User_data={this.state.User_data}/> : <Navigate to="/"  />}/>*/}
                </Routes> : <Routes>
                    <Route path='/' exact={'true'} element={<Navigate to={'/dashboard'}/>}/>
                    <Route path='/dashboard' element={<Main User_data={this.state.User_data}/>}>

                    </Route>
                    <Route path='/lessons' exact element={<Lessons User_data={this.state.User_data}/>}/>
                    {/*<Route path='*' element={<Navigate to={'/dashboard'}/>}/>*/}
                    {/*<Route path={'*'} >*/}
                    {/*    <Navigate replace to={'/'}/>*/}
                    {/*</Route>*/}
                    {/*<Route path='/dashboard' element={<Main User_data={this.state.User_data}/>}/>*/}
                    {/*<Route path='/lessons' element={<Lessons User_data={this.state.User_data}/>}/>*/}


                    {/*{this.state.Logged ? <Route path='/' element={<Welcome_page/>}/> :  <Navigate to="/dashboard"  />}*/}
                    {/*<Route  path='/dashboard' element={this.state.Logged ? <Main User_data={this.state.User_data}/> : <Navigate to="/"  />}/>*/}
                    {/*<Route  path='/lessons' element={this.state.Logged ? <Lessons User_data={this.state.User_data}/> : <Navigate to="/"  />}/>*/}
                </Routes>
                }
            </Router>
        );

    }
}

export default App;
