import React, {useEffect, useState} from "react";
import '../../styles/Manage/UsersList.css';
import logo from '../../images/user.jpg';
import serverPath from "../../utilis/server-path";

const SerachInput = (callback) => {
    const [Query, SetQuery] = useState('')

    useEffect(() => {
        const delayDebounceFn = setTimeout(() => {
            callback.callback(Query)
            // Send Axios request here
        }, 1000)

        return () => clearTimeout(delayDebounceFn)
    }, [Query])

    return (
        <input id={'ListUsers_search'} type={'text'} placeholder={'Szukaj'} onChange={(e) => SetQuery(e.target.value)}/>
    )

}

export default class Userlist extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            UserLoad: false,
            Users: [],
            PopState: 0,
            query_serach: '',
            SearchingMode: false
        }

        this.ranks = {
            1: 'Użytkownik',
            2: 'Nauczyciel',
            3: 'Administrator',
        }

    }

    componentDidMount() {
        this.getData()
    }

    SearchQuery(query) {
        if (!!query) {
            this.state.query_serach = query
            this.state.Users = []
            this.state.UserLoad = false
            this.Searching()
        } else {
            this.state.Users = []
            this.state.UserLoad = false
            this.getData()
        }
    }

    async Searching() {
        try {
            const resp = await fetch(serverPath() + 'api/Users/Search.php?page=0&&query=' + encodeURIComponent(this.state.query_serach)
                , {
                    headers: {
                        'Access-Control-Allow-Origin': '*'
                    },
                    credentials: 'include'
                }).then(e => e.json()).catch(null)

            if (resp !== null) {
                if (resp['CODE'] === 'OK') {
                    if (Object.keys(resp['Users'] ).length > 0) {
                        this.state.Users = this.state.Users.concat(resp['Users'])
                    } else this.state.UserLoad = true
                } else this.state.UserLoad = true
                this.forceUpdate()
            }
        }
        catch (e) {

        }
    }

    async getData() {
        try {
            const resp = await fetch(serverPath() + 'api/Users/?page=0', {
                headers: {
                    'Access-Control-Allow-Origin': '*'
                },
                credentials: 'include'
            }).then(e => e.json()).catch(null)

            if (resp !== null) {
                if (resp['CODE'] === 'OK') {
                    if (Object.keys(resp['Users']).length > 0) {
                        this.state.Users = this.state.Users.concat(resp['Users'])
                        this.forceUpdate()
                    } else this.state.UserLoad = true
                } else this.state.UserLoad = true
            }else this.state.UserLoad = true
        } catch (e) {
            this.state.UserLoad  = true
        }
    }

