import React from "react";
import '../styles/welcome.css';
import '../styles/login_themes.css'

class Welcome_page extends React.Component {

    constructor() {
        super();
        this.theme_id = Math.floor(Math.random() * (31 - 1)) + 1;
        this.state_view = 1;
    }

    login_inputs() {
        return (
            <div className="login_back">
                <h3>Logowanie</h3>
                <div className="login_back_body">
                    <div className="input_div">
                        <a>Adres e-mail</a>
                        <input type="text" placeholder="Adres e-mail"/>
                    </div>
                    <div className="input_div">
                        <a>Haslo</a>
                        <input type="password" placeholder="Hasło"/>
                    </div>
                    <label className="container">
                        <input type="checkbox"/>
                        <span className="checkmark"></span>
                        <a>
                            Zapamiętaj hasło
                        </a>
                    </label>
                    <span className="forgot_pass_bt">Przypomnij hasło</span>
                    <button className="login_button">Zaloguj się</button>
                </div>
                <a className="login_create_account">Nie posiadasz konta? <span>Stwórz je</span> </a>
            </div>
        )
    }

    register_inputs() {
        return (
            <div>

            </div>
        )
    }

    forgot_pass_intputs() {

    }

    switch_state() {
        if (this.state_view == 1) return this.login_inputs()
        else if (this.state_view == 2) return this.register_inputs()
        else return this.forgot_pass_intputs()
    }

    render() {
        return (
            <div className={'welcome_background theme_' + this.theme_id.toString()} >
                <div className="welcome_back">
                    <div className="welcome_text">
                        <div className="welcome_header">
                            <h1>Platforma Edukacyjna</h1>
                        </div>
                        <div className="welcome_body">
                            {this.switch_state()}
                        </div>
                    </div>
                </div>
            </div>
        )
    }

}

export default Welcome_page;
