import React, {useState} from "react";
import {Route, Navigate} from "react-router-dom";
import authorize from "./authorize";

export const ProtectedRoute = ({children, ...rest}) => {

    const [data, setDate] = useState({Logged: false, user_data: null, checked: false})

    authorize.CheckAuthorize().then(
        data => {
            if (data !== null) setDate({Logged: true, user_data: data, checked: true})
            else setDate({Logged: false, user_data: null, checked: true})
        }
    ).catch(() => {
        setDate({Logged: false, user_data: null, checked: true})
    })

    if (data.checked) {
        if (data.Logged) {
            return (React.cloneElement(children, {User_data : data.user_data}))
        } else {
            return (
                <Navigate replace to={'/'}/>
            )
        }
    } else return (<h1>Autoryzacja w toku</h1>)
}
