import React from "react";
import '../styles/lesson_view.css';

export default class Lesson extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
             ID: 0
        }
    }

    componentDidMount() {
        const params = new URLSearchParams(window.location.search)
        if (params.get('ID') !== undefined && params.get('ID') != '0') {
            this.setState({ID: parseInt(params.get('ID'))})
        }
    }

    render() {
        return(
            <div>
                <div>
                    <h1>Lekcja {this.state.ID} </h1>
                </div>
            </div>
        )
    }
}
