import './App.css';
import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate} from 'react-router-dom';
import Welcome_page from "./windows/welcom_page";
import Main from './windows/main';
import Lessons from "./windows/lessons";
import authorize from './utilis/authorize'

class App extends React.Component {

    constructor() {
        super();
        this.state = {
            Logged: false,
            User_data: null
        }
    }

    componentDidMount() {
        authorize.CheckAuthorize().then(
            data => this.setState({Logged: data !== null, User_data: data !== null ? data : null})
        )
    }

    render() {
        return (
            <Router>
                <Routes>
                    <Route exact path='/' element={this.state.Logged ? <Navigate to="/lessons" replace /> : <Welcome_page />} />
                    <Route path='/dashboard' element={this.state.Logged ? <Main User_data={this.state.User_data}/> : <Navigate to="/" replace />}/>
                    <Route path='/lessons' element={this.state.Logged ? <Lessons/> : <Navigate to="/" replace />}/>
                </Routes>
            </Router>
        );

    }
}

export default App;
