import crypto from "./crypto";

export default {

    async authorize(login_input, password_input) {
        try {
            password_input = await crypto.sha256(password_input)
            const resp = await fetch('http://192.168.0.7:8080/api/auth/login.php', {
                method: 'POST',
                headers: {
                    'Access-Control-Allow-Origin':'*',
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    login: login_input,
                    password: password_input,
                    Auto_login: false
                }),
                credentials: 'include'
            }).then(data => data.json()).catch(null)

            if (resp !== null) {
                if (resp["CODE"] === 'OK') {
                    window.sessionStorage.setItem('Token', resp['Token'])
                    return true
                } else {
                    return false
                }
            } else return false;
        } catch (e) {
            return false
        }
    },

